import './MyContent.css'

import { Divider, Button, Row, Icon, Modal } from 'antd'

import GraduationIcon from '../static/graduation-hat.svg'
import BriefcaseIcon from '../static/briefcase.svg'

import { useState } from 'react'

const tiles = {
    robot: {
        title: 'Autonomous Agricultural Robot',
        source: '../static/images/robot/robot0.jpeg',
    },
    solar: {
        title: 'PV Sales and Tech Support',
        source: '../static/images/solar/coolAndCasual.jpg',
    },
    rpa: {
        title: 'Robotic Process Automation (RPA)',
        source: '../static/images/rpa/diploma0.jpg',
    },
    bollos: {
        title: 'Industrial Automation',
        source: '../static/images/bollos/wincc.jpg',
    },
    dobot: {
        title: "Master's Internship",
        source: '../static/images/dobot/dobot0.jpg',
    }
}

// function enableTileOverflow() {
//     console.log('enableTileOverflow()')
//     document.getElementsByClassName('tiles-container')[0].style.overflow = 'visible'
// }

// function disableTileOverflow() {
//     console.log('disableTileOverflow()')
//     document.getElementsByClassName('tiles-container')[0].style.overflowY = 'visible'
//     document.getElementsByClassName('tiles-container')[0].style.overflowX = 'scroll'
// }

function enableTileOverflow() {
    console.log('enableTileOverflow()')
    document.getElementsByClassName('tiles-container')[0].style.clipath = '5%'
}

function disableTileOverflow() {
    console.log('disableTileOverflow()')
    document.getElementsByClassName('tiles-container')[0].style.left = 0
}

const Tile = ({ tile }) => {
    const [open, setOpen] = useState(false)

    return (
        <>
            {/* <div onMouseEnter={enableTileOverflow} onMouseOut={disableTileOverflow} className='tile' */}
            <div className='tile' onClick={() => setOpen(!open)}>
                <img className='tile-image' src={tile.source} />
                <div className='tile-details'>
                    <div className='tile-title'>{tile.title}</div>
                </div>
            </div>
            <Modal
                title="Vertically centered modal dialog"
                centered
                visible={open}
                // onOk={() => this.setModal2Visible(false)}
                // onCancel={() => this.setModal2Visible(false)}
                zIndex={99999999}
                mask={true}
                maskClosable={true}
            >
                <p>some contents...</p>
                <p>some contents...</p>
                <p>some contents...</p>
            </Modal>
        </>
    )
}

const experiences = {
    masters : {
        id: 'masters',
        position: "Master's Degree in Electromechanical Engineering",
        date: 'February 2019',
        organization: 'ETS Ingeniería y Diseño Industrial, Universidad Politécnica de Madrid',
        location: 'Madrid, Spain',
        bullets: [
            "Master's Thesis - Autonomous Agricultural Robot",
            'Programming Arduinos (I2C, MQTT, control PID) and Raspberry Pis (Flask, MongoDB)',
            'Programming Siemens PLC with STEP7 y WinCC',
            'Programming with NI LabView',  
            'Programming robot kinematics with MATLAB',
            'Simulation of electrical (QUCS) and mechanical (Festo FluidSIM, SAM) systems'
        ]
    },
    coursesETSIDI : {
        position: 'Undergraduate Courses',
        date: 'Fall 2015 - Spring 2016',
        organization: 'ETS Ingeniería y Diseño Industrial, Universidad Politécnica de Madrid',
        location: 'Madrid, Spain',
        bullets: [
            'Electronics, Automation, Circuit Theory, Thermodynamics, Fluids Mechanics'    
        ]
    },
    coursesMC : {
        position: 'Undergraduate Courses',
        date: 'Spring 2014 - Spring 2015',
        organization: 'Montgomery College',
        location: 'Maryland, USA',
        bullets: [
            'Differential Equations, Physics III'    
        ]
    },
    undergrad: {
        id: 'undergrad',
        position: "Bachelor's Degree in Environmental Science and Technology",
        date: 'December 2013',
        organization: 'University of Maryland, College Park',
        location: 'Maryland, USA',
        bullets: [
            'Graduated cum laude',
            'Minored in Ecological Technology Design and Geographic Information Systems',
            'Designed and constructed an anaerobic digestor for the university campus',
            'Restoration of the Baltimore Harbor in collaboration with Biohabitats, Inc'
        ]
    },
    associates: {
        position: "Associates's Degree in Environmental Science and Policy",
        date: 'Spring 2011',
        organization: 'Montgomery College',
        location: 'Maryland, USA',
        bullets: [
            "First 2 years of my Bachelor's Degree completed at my local community college"
        ]
    },
    montes: {
        position: "Bachelor's Degree in Forestry Engineering",
        date: 'Spring 2009',
        organization: 'Montgomery College',
        location: 'Maryland, USA',
        bullets: [
            'First and second year before transferring to the United States'
        ]
    },
    solar1: {
        id: 'solar1',
        position: 'Photovoltaic Sales and Technical Support',
        date: 'Junes 2013 - June 2015',
        organization: 'Solar Solution LLC',
        location: 'Washington, DC, USA',
        bullets: [
            'PV system design (residential and commercial) with SketchUp, SolarNexus, and Google Earth',
            'Automated document generation (JavaScript, Excel, VBA) of proposals, interconnection agreements, and contracts',
            'Participation in local electrical inspections',
            'On-site technical support and troubleshooting'
        ]
    },
    solar2 : {
        id: 'solar2',
        position: 'Photovoltaic Apprentice',
        date: 'Junes 2013',
        organization: 'Earth Sun Energy Systems',
        location: 'West Virginia, USA',
        bullets: [
            'Assistance in the construction of a 24-panel system'
        ]
    },
}

const skills = {
    english: {
        id: 'english',
        name: 'Native English and Spanish',
        bullets: [
            'Native English (and Spanish) speaking, writing, and reading',
            'Schooling abroad, especially university in the USA, as well as several years of working there',
        ]
    },
    software: {
        id: 'rpa',
        name: 'Software',
        bullets: [
            'UiPath (RPA Developer Advanced Certification 2019)',
            'VBA macros in Word and Excel, JavaScript in Adobe PDF',
            'Siemens STEP7 and WinCC',
            'Google SketchUp',
            'LabView',
            'MATLAB',
            'QUCS',
            'FluidSIM',
            'SAM'
        ]
    },
    coding: {
        id: 'coding',
        name: 'Programming Languages',
        bullets: [
            'Python (especially Flask )',
            'C (C++ if you count the Arduino language)',
            'SQL (SQLite3, PostgreSQL)',
            'Javascript (especially ReactJS)',
            'HTML, CSS',
            'Standard use of Linux, SSH, and git',
            'Basic knowledge of x86 assembly (AT&T)'
        ]
    }
}

const Experience = ({ experience }) => (
    <div id={experience.id} className='exp-skill'>
        <Row type="flex" justify="space-between" align='middle' className='butt'>
            <div className='position'>{experience.position}</div>
            <div>{experience.date}</div>                
        </Row>
        <Row>
            {experience.organization}, <span className='location'>{experience.location}</span>
        </Row>
        <ul>
            {experience.bullets.map((bullet) => <li>{bullet}</li>)}
        </ul>
    </div>
)

const Skill = ({ skill }) => (
    <div id={skill.id} className='exp-skill'>
        <Row type="flex" justify="start" align='middle' className='position'>
            {skill.name}
        </Row>
        <ul>
            {skill.bullets.map((detail) => <li>{detail}</li>)}
        </ul>
    </div>
)

