    console.log('disableTileOverflow()')
    document.getElementsByClassName('tiles-container')[0].style.left = 0
}



const Tile = ({ tile }) => {
    const [open, setOpen] = useState(false)

    return (
        <>
            {/* <div onMouseEnter={enableTileOverflow} onMouseOut={disableTileOverflow} className='tile' */}
            <div className='tile' onClick={() => setOpen(!open)}>
                <img className='tile-image' src={tile.source} />
                <div className='tile-details'>
                    <div className='tile-title'>{tile.title}</div>
                </div>
            </div>
            {/* <MyModal /> */}
            <Modal
                title={tile.title}
                centered
                visible={open}
                onOk={() => setOpen(true)}
                onCancel={() => setOpen(false)}
                zIndex={99999}
                mask={true}
                closable={false}
                footer={null}
                // maskClosable={true}
            >
                {tile.description.paragraphs && 
                    tile.description.paragraphs.map((paragraph) => <p>{paragraph}</p>)
                }
                {tile.description.bullets && 
                    <ul>
                        {tile.description.bullets.map((bullet) => <li>{bullet}</li>)}
                    </ul>
                }
                {tile.slides && 
                    <Carousel>
                        <div>
                            <h3>1</h3>
                        </div>
                        <div>
                            <h3>2</h3>
                        </div>
                        <div>
                            <h3>3</h3>
                        </div>
                        <div>
                            <h3>4</h3>
                        </div>
                    </Carousel>
                }
            </Modal>
        </>
    )
}

